import { useEffect, useState } from 'react'
import classes from './Players.module.css';
import axios from 'axios'

const Players = () => {

  const [players, setPlayers] = useState([]);

  useEffect(() => {
    getPlayers();
  }, []);

  const getPlayers = () => {
    try {
      const response = axios.get('http://localhost:5000/players')
      setPlayers(response.data)
    } catch (error) {
      console.log(error)
    }
  }

  const deletePlayer = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/teams/${id}`)
      setPlayers(players.filter(player => player.id !== id))
    } catch (error) {
      console.log(error)
    }
  }
  return (
    <div>
      <h2 className='text-center'>Players</h2>
            <Link to="/Teams/add" className="button is-primary mt-2">Add New</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No.</th>
                        <th>Team</th>
                        <th>Name</th>
                        <th>P</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {teams.map((team, index) => (
                        <tr key={team.id}>
                            <td>{index + 1}.</td>
                            <td>{team.team_name}</td>
                            <td>
                                <Link to={`/Teams/edit/${team.id}`} >Edit</Link>
                                <button onClick={() => deleteTeam(team.id)} className="button is-small is-danger">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
    </div>
  )

}


export default Players;
