import React, { Component, useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate, useParams, withRouter } from 'react-router-dom';

class GetTeam extends React.Component {
    constructor(props) {
        super(props)
        id : this.props.match.params.id;
        this.state = {
            teams: []
        }
    }




    componentDidMount() {
        try {
            const response = axios.get(`http://localhost:5000/teams/${id}`)
            console.log(response)
            this.setState({ teams: response.data.teamPlayers })
        } catch (error) {
            console.log(error)
        }
    }

    render() {
        const { teams } = this.state
        return (
            <div>
                {teams.map(team =>
                    <div key={team.id}>
                        {team.teamPlayers}
                    </div>
                )}


            </div>
        )

    }
}

export default GetTeam
