import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from 'yup'
import './Teams.css'

const EditResult = () => {
    const [host_id, setHostId] = useState([]);
    const [guest_id, setGuestId] = useState([]);
    const [home_goals, setHomeGoals] = useState([]);
    const [away_goals, setAwayGoals] = useState([]);
    const [date, setDate] = useState(new Date());
    const [venue, setVenue] = useState('')
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        getResultById();
    }, []);

    const getTeamsId = async () => {
        const response = await axios.get('http://localhost:5000/teams')
        const teams = response.data.map(team => team.id).flat();
        setHostId(teams)
        setGuestId(teams)
    }

    const updateResult = () => {
        axios.patch(`http://localhost:5000/results/${id}`, {
            host_id: host_id,
            guest_id: guest_id,
            home_goals: home_goals,
            away_goals: away_goals,
            date: date,
            venue: venue,
        });
        navigate("/Results");
    }


    const getResultById = () => {
        const response = axios.get(`http://localhost:5000/results/${id}`);
        setHostId(response.host_id)
        setGuestId(response.guest_id)
        setHomeGoals(response.home_goals)
        setAwayGoals(response.away_goals)
        setDate(response.date)
        setVenue(response.venue)
    }

    return (
        <div>
            <form onSubmit={updateResult}>
                <div className="field">
                    <label className="label">Host Id:</label>
                    <input
                        className="input"
                        type="number"
                        value={host_id}
                        onChange={(event) => {
                            setHostId(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Guest Id:</label>
                    <input
                        className="input"
                        type="number"
                        value={guest_id}
                        onChange={(event) => {
                            setGuestId(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Home Goals:</label>
                    <input
                        className="input"
                        type="number"
                        value={home_goals}
                        onChange={(event) => {
                            setHomeGoals(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Away Goals:</label>
                    <input
                        className="input"
                        type="number"
                        value={away_goals}
                        onChange={(event) => {
                            setAwayGoals(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Date:</label>
                    <input
                        className="input"
                        type="date"
                        value={date}
                        onChange={(event) => {
                            setDate(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Venue:</label>
                    <input
                        className="input"
                        type="text"
                        value={venue}
                        onChange={(event) => {
                            setVenue(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <button className="button is-primary">Update</button>
                </div>
            </form>
        </div>
    )
}

export default EditResult
