import { useState, useEffect } from 'react'
import axios from "axios";
import { renderMatches, useNavigate, useParams } from 'react-router-dom';

const Team = () => {

    const { id } = useParams();
    this.state = {
        teams: []
    }

    useEffect(() => {
        getTeamById();
    }, []);

    const getTeamById = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/teams/${id}`)
            console.log(response)
            this.state({ teams: response.data })
        } catch (error) {
            console.log(error)
        }
    }
    
    const { teams } = this.state
    return (
        <div>
            <tbody>{teams.map(team =>
                <tr key={team.id}>
                    <td>{team.homeResults}</td>
                </tr>
            )}
            </tbody>

        </div>
    )
            
}

export default Team
