import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';

const TeamPlayersById = () => {
    const [teamPlayersById, setTeamPlayersById] = useState([]);
    const { id } = useParams();
    useEffect(() => {
        getTeamPlayersById();
    })

    const getTeamPlayersById = async () => {
        try {
       const response = await axios.get(`http://localhost:5000/team-players/${id}`)
        setTeamPlayersById(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    return (
        <div>
          <h2>Players</h2>
        <thead>
          <tr>
            <th>â„–</th>
            <th>Team Id</th>
            <th>Name</th>
            <th>Position</th>
            <th>Age</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {teamPlayersById.map((value, index) => (
            <tr key={value.id}>
              <td>{index + 1}.</td>
              <td>{value.teamId}</td>
              <td>{value.name}</td>
              <td>{value.position}</td>
              <td>{value.age}</td>
              </tr>
          ))}
              </tbody>
        </div>
    )
}

export default TeamPlayersById
