import { useState, useEffect } from 'react'
import axios from "axios";
import { Link,  } from "react-router-dom";

const Teams = () => {
    const [teams, setTeams] = useState([]);

    useEffect(() => {
        getTeams();
    }, []);

    const getTeams = async () => {
     const response = await  axios.get('http://localhost:5000/teams')
                setTeams(response.data)
            
    }
    const deleteTeam = async (id) => {
      await  axios.delete(`http://localhost:5000/teams/${id}`)
    getTeams()
}
    return (
        <div>
            <h2 className='text-center'>Clubs</h2>
            <Link to="/Teams/add" className="button is-primary mt-2">Add New</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No.</th>
                        <th>Team</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {teams.map((team) => (
                        <tr key={team.id}>
                            <td>{index+1}.</td>
                            <td>{team.team_name}</td>
                            <td>
                                <Link to={`/Teams/edit/${team.id}`} >Edit</Link>
                                <button onClick={ () => deleteTeam(team.id) } className="button is-small is-danger">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}

export default Teams
