import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';

    class GetTeam extends React.Component {
        constructor(props) {
            super(props)

            this.state ={
                teams: []
            }
        }


 

    componentDidMount ()  {
        const { id } = this.props.match.params
        try {
            const response =  axios.get(`http://localhost:5000/teams/${id}`)
            console.log(response)
            this.state({teams:response.data.teamPlayers})
                } catch (error) {
            console.log(error)    
        }
    }

    render () {
        const {teams} =this.state
    return (
        <div>
             <tbody>{teams.map(team => 
               <tr key={team.id}>
             <td>{team.teamPlayers}</td>
             </tr>
             )}
            </tbody>
             
        </div>
    )

}
        }
    
    export default GetTeam
