import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';

const Team = () => {
    const [homeResults, sethomeResults] = useState([]);
    const { id } = useParams();
    const [results, setResult] = useState();
    useEffect(() => {
        getTeamById();
    }, []);

    const getTeamById = async () => {

        await axios.get(`http://localhost:5000/teams/${id}`)
            .then((response) => {
                let home = response.data.homeResults
                sethomeResults(home)



            })






    }

    return (
        <div>
            <thead>
                <tr>
                    <th>â„–</th>
                    <th>Host Id</th>
                    <th>Guest Id</th>
                    <th>Home Goals</th>
                    <th>Away Goals</th>
                    <th>Date</th>
                    <th>Venue</th>
                </tr>
            </thead>
            <tbody>
                {homeResults.map((hres, index) => (
                    <tr key={hres.id}>
                        <td>{index + 1}.</td>
                        <td>{hres['h.host_id']}</td>
                        <td>{hres['h.guest_id']}</td>
                        <td>{hres['h.home_goals']}</td>
                        <td>{hres['h.away_goals']}</td>
                        <td>{hres['h.date']} </td>
                        <td>{hres['h.venue']}</td>

                    </tr>
                ))}
            </tbody>

        </div>
    )

}

export default Team
