import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';

    class GetTeam extends React.Component {
        constructor(props) {
            super(props)

            this.state ={
                teams: []
            }
        }


 

    componentDidMount ()  {
        const { id } = useParams();        try {
            const response =  axios.get(`http://localhost:5000/teams/${id}`)
            console.log(response)
            this.setState({teams:response.data.teamPlayers})
                } catch (error) {
            console.log(error)    
        }
    }

    render () {
        const {teams} =this.state
    return (
        <div>
            {teams.map(team => 
               <div key={team.id}>
             {team.teamPlayers}
             </div>
             )}
            
             
        </div>
    )

}
        }
    
    export default GetTeam
