import { useEffect, useState } from 'react'
import { Link, } from "react-router-dom";
import axios from 'axios'
import './'
const Results = () => {
  const [results, setResults] = useState([]);

  useEffect(() => {
    getResults();
  }, []);

  const getResults = async () => {
    try {
      const response = await axios.get('http://localhost:5000/results')
      setResults(response.data)
    } catch (error) {
      console.log(error)
    }
  }

  const deleteResult = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/results/${id}`)
      setResults(results.filter(result => result.id !== id))
    } catch (error) {
      console.log(error)
    }
  }
  return (
    <div className={styles.App}>
      <h2 className='text-center'>Results</h2>
      <Link to="/Results/add" className="button is-primary mt-2">Add New</Link>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>№</th>
            <th>Home Team</th>
            <th>Away Team</th>
            <th>Home Goals</th>
            <th>Away Goals</th>
            <th>Date</th>
            <th>Venue</th>
          </tr>
        </thead>
        <tbody>
          {results.map((result, index) => (
            <tr key={result.id}>
              <td>{index + 1}.</td>
              <td>{result.host_id}</td>
              <td>{result.guest_id}</td>
              <td>{result.home_goals}</td>
              <td>{result.away_goals}</td>
              <td>{result.date}</td>
              <td>{result.venue}</td>
              <td>
                <Link to={`/Results/edit/${result.id}`} >Edit</Link>
                <button onClick={() => deleteResult(result.id)}
                  className="button is-small is-danger">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Link to={'/'}>Back To Home Page</Link>
    </div>
  )

}

export default Results;
