import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from 'yup'
import './Teams.css'


const AddTeam = () => {
    const navigate = useNavigate();
    const [teams, setTeam] = useState([]);
    useEffect(() => {
        getTeam();
    }, []);

    const getTeam = async () => {
        const response = await axios.get('http://localhost:5000/teams')
        const team = response.data.map(team => team.team_name).flat();
        console.log(team)
        setTeam(team)
    }
    const initialValues = {
        team_name: "",
    };

    const validationSchema = Yup.object().shape({
        team_name: Yup.string().required().min(3).max(20)
        .when(teams,(teams, schema) => {
            return schema.test({
                test: team_name => team_name !== team,
                message: 'Team with this name already exists!'
            })
        }),
    });

    const onSumbit = (data) => {
        axios.post('http://localhost:5000/teams', data)
        navigate('/Teams')
    }
    return (
        <div className="createTeamPage">
            <Formik
                initialValues={initialValues}
                onSumbit={onSumbit}
                validationSchema={validationSchema}
            >
                <Form className="formContainer">
                    <label>Team Name:</label>
                    <ErrorMessage name="team_name" component="span" />
                    <Field
                        autocomplete="off"
                        id="inputeCreateTeam"
                        name="team_name"
                    />
                    <button type="submit">Create Team</button>
                </Form>
            </Formik>
        </div>
    );
}

export default AddTeam
