    const [teams, setTeams] = useState([]);
    const {id } =useParams();

    useEffect(() => {
        getTeamById();
    }, []);

    const getTeamById = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/teams/${id}`)
            console.log(response)
            setTeams(response.data)
                } catch (error) {
            console.log(error)    
        }
    }
const items= []

for (const [index,value] of teams.entries()) {
