import { useState, useEffect } from 'react'
import axios from "axios";
import { Link, useParams } from "react-router-dom";

const Teams = () => {
    const [teams, setTeams] = useState([]);
    const { id } = useParams();

    useEffect(() => {
        getTeams();
    }, []);

    const getTeams = () => {
        axios.get('http://localhost:5000/teams')
            .then((response) => {
                console.log(response.data)
                setTeams(response.data)
            })
    }
    const deleteTeam = async (id) => {
      await  axios.delete(`http://localhost:5000/teams/${id}`)
            
            getTeams()
    }
    return (
        <div>
            <Link to="/Teams/add" className="button is-primary mt-2">Add New</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Team Name</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {teams.map((teams, index) => (
                        <tr key={teams.id}>
                            <td>{index + 1}</td>
                            <td>{teams.team_name}</td>
                            <td>
                                <Link to={`/Teams/edit/${teams.id}`} >Edit</Link>
                                <button onClick={() => deleteTeam(teams.id)} className="button is-small is-danger">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}

export default Teams
