import { useState, useEffect } from 'react'
import axios from "axios";
import { Link } from "react-router-dom"
import styles from "./TeamPlayers.module.css"
import Table  from 'react-bootstrap/Table'

const TeamPlayers = () => {
    const [teamPlayers, setTeamPlayers] = useState([]);
    const [teams, setTeams] = useState([]);
    useEffect(() => {
        getTeamPlayers()
    }, []);

    useEffect(() => {
        getTeams()
    }, []);

    const getTeamPlayers = async () => {
        try {
            const response = await axios.get('http://localhost:5000/team-players')
            setTeamPlayers(response.data)
        } catch (error) {
            console.log(error)
        }
    }
    const getTeams = async () => {
        try {
            const response = await axios.get('http://localhost:5000/teams')
            setTeams(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    return (
        <div className={styles.App}>
            <h2 >All Team Players</h2>
            <Table striped bordered hover>  
            <thead>
                <tr>
                    <tr>№FDV</tr>
                    <tr>Team Id</tr>
                    <th>Team Name</th>
                    <th>Name</th>
                    <th>Age</th>
                    <th>Position</th>
                </tr>
            </thead>
            <tbody>
                {teamPlayers.map((value, index) => (
                    <tr key={value['p.id']}>
                        <td>{index + 1}.</td>
                        <td>{value['p.teamId']}</td>
                        <td>{value['team_name']}</td>
                        <td>{value['p.name']}</td>
                        <td>{value['p.age']}</td>
                        <td>{value['p.position']}</td>
                    </tr>
                ))}
            </tbody>
            </Table>
            <div>
                <h2>View Team Players</h2>
                <Table striped bordered hover>  
                <thead>
                    <th>№</th>
                    <th>Team</th>
                </thead>
                <tbody>
                    {teams.map((value, index) => (
                        <tr key={value['id']}>
                            <td>{index + 1}.</td>
                            <td>{value['team_name']}</td>
                            <td>
                                <Link to={`/Team-Players/${value.id}`}>View</Link>
                            </td>
                        </tr>
                    ))}
                </tbody>
                </Table>
            </div>
        </div>
    )
}

export default TeamPlayers
