import { useState, useEffect } from 'react'
import axios from "axios";
import { Link, } from "react-router-dom";
import styles from './Teams.module.css'


const Teams = () => {
    const [teams, setTeams] = useState([]);

    useEffect(() => {
        getTeams();
    }, []);

    const getTeams = async () => {
        try {
            const response = await axios.get('http://localhost:5000/teams')
            setTeams(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    const deleteTeam = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/teams/${id}`)
            setTeams(teams.filter(team => team.id !== id))
        } catch (error) {
            console.log(error)
        }
    }
    return (
        <div className={styles.App}>
            <h2>Clubs</h2>
            <Link to="/Teams/add" className="button is-primary mt-2">Add New</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr >
                        <th>â„–</th>
                        <th>Team</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody className={styles.body}>
                    {teams.map((team, index) => (
                        <tr key={team.id}>
                            <td className={styles.body}>{index + 1}.</td>
                            <td>{team.team_name}</td>
                            <td>
                                <Link to={`/Teams/${team.id}`}>View</Link>
                                <Link to={`/Teams/edit/${team.id}`} >Edit</Link>
                                <button onClick={() => deleteTeam(team.id)}
                                    className="button is-small is-danger">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
                
            </table>
        </div>
    )
}

export default Teams
