import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';

const Team = () => {
    const [teams, setTeams] = useState([]);
    const { id } = useParams();

    useEffect(() => {
        getTeamById();
    }, []);

    const getTeamById = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/teams/${id}`)
            console.log(response)
            setTeams(response.data)
        } catch (error) {
            console.log(error)
        }
    }
    const itemRows = [];
    for (let team of teams) {
      const row = (
        <tr key={team.id}>
          <td key={1}>{team.id}</td>
          <td key={2}>{team.name}</td>
          <td key={3}>{team.age}</td>
        </tr>
      );
      itemRows.push(row);
    }
    return (
        <div>
            <tbody>{Object.keys(teams).map(team =>
                <tr key={team.id}>
                    <td>{team}</td>
                </tr>
            )}
            {}
            </tbody>

        </div>
    )

}

export default Team
