import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from 'yup'
import './Players.css'


const AddPlayer = () => {
    const [teams, setTeams] = useState([]);
    const navigate = useNavigate();
    useEffect(() => {
        getTeamId();
        getPlayers();
    }, []);


    const getTeamId = async () => {
        const response = await axios.get('http://localhost:5000/teams')
        const teamId = response.data.map(team => team.id).flat();
        setTeams(teamId)
    }

    const initialValues = {
        teamId: "",
        name: "",
        position: "",
        age: "",
    };

    const validationSchema = Yup.object().shape({
        teamId: Yup.number().required().oneOf(teams, 'No such team with this id'),
        name: Yup.string().required().min(3).max(15),
        position: Yup.string().min(2).max(10).required(),
        age: Yup.number().min(15).max(45).required(),
    });

    const onSubmit = (data) => {
        axios.post('http://localhost:5000/players', data)
        navigate('/Players')
    }

    return (
        <div className="createPlayerPage">
            <Formik
                initialValues={initialValues}
                onSubmit={onSubmit}
                validationSchema={validationSchema}
            >
                <Form className="formContainer">
                    <label>Team Id: </label>
                    <ErrorMessage name="teamId" component="span" />
                    <Field
                        autocomplete="off"
                        id="inputCreatePlayer"
                        name="teamId"
                    />
                    <label>Name: </label>
                    <ErrorMessage name="name" component="span" />
                    <Field
                        autocomplete="off"
                        id="inputCreatePlayer"
                        name="name"
                    />
                    <label>Position: </label>
                    <ErrorMessage name="position" component="span" />
                    <Field
                        autocomplete="off"
                        id="inputCreatePlayer"
                        name="position"
                    />
                    <label>Age: </label>
                    <ErrorMessage name="age" component="span" />
                    <Field
                        autocomplete="off"
                        id="inputCreatePlayer"
                        name="age"
                    />
                    <button type="submit"> Create Player</button>
                </Form>
            </Formik>
        </div>
    );
}

export default AddPlayer
