import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from 'yup'
import './Teams.css'

const EditTeam = () => {
    const [team, setTeam] = useState('');
    const navigate = useNavigate();
    const { id } = useParams();
    useEffect(() => {
        getTeamById();
    }, []);

    const getTeams = async () => {
        const response = await axios.get('http://localhost:5000/teams')
        const team = response.data.map(team => team.team_name).flat();
        setTeam(team)
    }

    const initialValues = {
        team_name: "",
    };

    const validationSchema = Yup.object().shape({
        team_name: Yup.string().required().min(3).max(20)
        .notOneOf(team_name, 'Team with this name exists!')
    });
    const onSubmit = (data) => {
        axios.patch(`http://localhost:5000/teams/${id}`, data) 
        navigate("/");
    }

    return (
        <div className="createTeamPage">
            <Formik
                initialValues={initialValues}
                onSubmit={onSubmit}
                validationSchema={validationSchema}
            >
                <Form className="formContainer">
                    <label>Team Name:</label>
                    <ErrorMessage name="team_name" component="span" />
                    <Field
                        autocomplete="off"
                        id="inputeCreateTeam"
                        name="team_name"
                    />
                    <button type="submit">Edit</button>
                </Form>
            </Formik>
        </div>
    )
}

export default EditTeam
