import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from 'yup'
import './Teams.css'


const AddTeam = () => {
    const navigate = useNavigate();
    const [teams, setTeam] = useState([]);
    useEffect(() => {
        getTeams();
    }, []);

    const getTeams = async () => {
        const response = await axios.get('http://localhost:5000/teams')
        const team = response.data.map(team => team.team_name).flat();
        setTeam(team)
    }

    const initialValues = {
        team_name: "",
    };

    const validationSchema = Yup.object().shape({
        team_name: Yup.string().required().min(3).max(20)
            .notOneOf(teams, 'Team with this name exists!')
    });

    const onSumbit = (data) => {
        axios.post('http://localhost:5000/teams', data)
        navigate('/')
    }
    return (
        <div className="createTeamPage">
            <Formik
                initialValues={initialValues}
                onSubmit={onSumbit}
                validationSchema={validationSchema}
            >
                <Form className="formContainer">
                    <label>Team Name:</label>
                    <ErrorMessage name="team_name" component="span" />
                    <Field
                        autocomplete="off"
                        id="inputCreateTeam"
                        name="team_name"
                    />
                    <button type="submit">Create Team</button>
                    <Link to={'/teams'} className='link'>Cancel</Link>

                </Form>
            </Formik>
        </div>
    );
}

export default AddTeam
