import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate, useParams, Link } from 'react-router-dom';
import Table  from 'react-bootstrap/Table'

const TeamPlayersById = () => {
    const [teamPlayersById, setTeamPlayersById] = useState([]);
    const { id } = useParams();

    useEffect(() => {
        getTeamPlayersById();
    })

    const getTeamPlayersById = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/team-players/${id}`)
            setTeamPlayersById(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    return (
        <div>
            <h2>Players</h2>
            <Table striped bordered hover>
            <thead>
                <tr>
                    <th>â„–</th>
                    <th>Name</th>
                    <th>Position</th>
                    <th>Age</th>
                </tr>
            </thead>
            <tbody>
                {teamPlayersById.map((value, index) => (
                    <tr key={value['p.id']}>
                        <td>{index + 1}.</td>
                        <td>{value['p.name']}</td>
                        <td>{value['p.age']}</td>
                        <td>{value['p.position']}</td>
                    </tr>
                ))}
                <div> <Link to={'/Team-Players'}>Back To Team Players</Link> </div>
                <div> <Link to={'/'}>Home Page</Link> </div>
            </tbody>
            /</Table>
        </div>
    )
}

export default TeamPlayersById
