import React, {Component} from 'react'
import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';

class GetTeam extends React.Component {
    constructor(props) {
        super (props)
    

    this.state = {
        teams:[]
    }
}
componentDidMount(event) {
  const response =  axios.get(`http://localhost:5000/teams/${event.target.id.value}`)
this.setState({teams:response.data})
}

render () {
    const {teams} =this.state
    const {id} =this.state
    return (
        <div>
{teams.map(team => <div key={team.id}>{team.teamPlayers}</div>)}
        </div>
    )
}

}

export default GetTeam
