import { useState } from 'react'
import axios from "axios";
import { useNavigate } from 'react-router-dom';

const AddResult = () => {
    const [host_id, setHostId] = useState(0);
    const [guest_id, setGuestId] = useState(0);
    const [home_goals, setHomeGoals] = useState(0);
    const [away_goals, setAwayGoals] = useState('0');
    const [date, setDate] = useState(0);
    const [venue, setVenue] = useState('')
    const navigate = useNavigate();

    const saveResult = async () => {
        axios.post('http://localhost:5000/results', {
            host_id: host_id,
            guest_id: guest_id,
            home_goals: home_goals,
            away_goals: away_goals,
            date: date,
            venue: venue,
        });
        navigate("/Results");
    }

    return (
        <div>
            <form onSubmit={saveResult}>
                <div className="field">
                    <label className="label">Host Id:</label>
                    <input
                        className="input"
                        type="number"
                        value={host_id}
                        onChange={(event) => {
                            setHostId(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Guest Id:</label>
                    <input
                        className="input"
                        type="number"
                        value={guest_id}
                        onChange={(event) => {
                            setGuestId(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Home Goals:</label>
                    <input
                        className="input"
                        type="number"
                        value={home_goals}
                        onChange={(event) => {
                            setHomeGoals(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Away Goals:</label>
                    <input
                        className="input"
                        type="number"
                        value={away_goals}
                        onChange={(event) => {
                            setAwayGoals(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Date:</label>
                    <input
                        className="input"
                        type="date"
                        value={date}
                        onChange={(event) => {
                            setDate(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Venue:</label>
                    <input
                        className="input"
                        type="text"
                        value={venue}
                        onChange={(event) => {
                            setVenue(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <button className="button is-primary">Save</button>
                </div>
            </form>
        </div>
    )
}

export default AddResult
