import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';

const EditPlayer = () => {
    const [team, setTeam] = useState([]);
    const [name, setName] = useState([]);
    const [teamId, setTeamId] = useState([]);
    const [position, setPosition] = useState([]);
    const [age, setAge] = useState([]);
    const navigate = useNavigate();
    const { id } = useParams();
    useEffect(() => {
        getTeamId();
        getPlayerById();
    }, []);

    const getTeamId = async () => {
        const response = await axios.get('http://localhost:5000/teams')
        const team = response.data.map(team => team.id).flat();
        setTeam(team)
    }

    const initialValues = {
        team_n: "",
    };

    const updatePlayer = () => {
        axios.patch(`http://localhost:5000/players/${id}`, {
            name: name,
            teamId: teamId,
            position: position,
            age: age,
        });
        navigate("/Players");
    }


    const getPlayerById = () => {
        const response = axios.get(`http://localhost:5000/players/${id}`);
        setName(response.name);
        setTeamId(response.teamId);
        setPosition(response.position);
        setAge(response.age);
    }

    return (
        <div>
            <form onSubmit={updatePlayer}>
                <div className="field">
                    <label className="label">Player name:</label>
                    <input
                        className="input"
                        type="text"
                        value={name}
                        onChange={(event) => {
                            setName(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Team Id:</label>
                    <input
                        className="input"
                        type="number"
                        value={teamId}
                        onChange={(event) => {
                            setTeamId(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Position:</label>
                    <input
                        className="input"
                        type="text"
                        value={position}
                        onChange={(event) => {
                            setPosition(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Age:</label>
                    <input
                        className="input"
                        type="number"
                        value={age}
                        onChange={(event) => {
                            setAge(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <button className="button is-primary">Update</button>
                </div>
            </form>
        </div>
    )
}

export default EditPlayer
