import { useState } from 'react'
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import styles from './Results.module.css'
const AddResult = () => {
    const [host_id, setHostId] = useState(0);
    const [guest_id, setGuestId] = useState(0);
    const [home_goals, setHomeGoals] = useState(0);
    const [away_goals, setAwayGoals] = useState(0);
    const [date, setDate] = useState(new Date());
    const [venue, setVenue] = useState('')
    const navigate = useNavigate();

    const saveResult = async () => {
     await axios.post('http://localhost:5000/results', {
            host_id: host_id,
            guest_id: guest_id,
            home_goals: home_goals,
            away_goals: away_goals,
            date: date,
            venue: venue,
        });
        navigate("/Results");
    }

    return (
        <div className={styles.App}>
            <div className={styles.information}>
            <h2>Add New Result</h2>
            <form onSubmit={saveResult}>
                <div className="field">
                    <label className="label">Host Id:</label>
                    <input
                        className={styles.inputHg}
                        margin-left='100px'
                        type="number"
                        value={host_id}
                        onChange={(event) => {
                            setHostId(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Guest Id:</label>
                    <input
                        className={styles.inputHg}
                        type="number"
                        value={guest_id}
                        onChange={(event) => {
                            setGuestId(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Home Goals:</label>
                    <input
                        className={styles.input}
                        type="number"
                        value={home_goals}
                        onChange={(event) => {
                            setHomeGoals(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Away Goals:</label>
                    <input
                        className={styles.input}
                        type="number"
                        value={away_goals}
                        onChange={(event) => {
                            setAwayGoals(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label>Date:</label>
                    <input
                        className={styles.input}
                        type="date"
                        value={date}
                        onChange={(event) => {
                            setDate(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <label className="label">Venue:</label>
                    <input
                        className={styles.input}
                        type="text"
                        value={venue}
                        onChange={(event) => {
                            setVenue(event.target.value)
                        }} />
                </div>
                <div className="field">
                    <button className="button is-primary">Save</button>
                </div>
            </form>
            </div>
        </div>
    )
}

export default AddResult
